def get_page_text(spec_key, page_index):
    """
    Returneaza textul pentru `spec_key` (ex: 'CTI') si pagina `page_index` (0-based).
    Daca nu exista, returneaza un text fallback.
    """
    if not spec_key:
        return ""
    pages = PAGES.get(spec_key, [])
    if not pages:
        return f"{spec_key}\n\n(Detalii indisponibile)"
    idx = max(0, min(page_index, len(pages) - 1))
    return pages[idx]